// src/components/categories/CategoriesListPage.js
import React, { useState, useEffect } from 'react';
import CategoryService from '../../services/CategoryService';
import { useAuth } from '../../AuthContext';
import { useNavigate } from 'react-router-dom';
import './CategoriesListPage.css';


const CategoriesListPage = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { token, isAdmin } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const data = await CategoryService.getAllCategories(token);
        setCategories(data);
      } catch (err) {
        setError('Failed to fetch categories');
      } finally {
        setLoading(false);
      }
    };
    fetchCategories();
  }, [token]);

  const handleDelete = async (id) => {
    try {
      await CategoryService.deleteCategory(id, token);
      setCategories(categories.filter(category => category.id !== id));
    } catch (err) {
      setError('Failed to delete category');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="categories-list">
      <h1>Categories</h1>
      {isAdmin && (
        <button onClick={() => navigate('/admin/categories/add')}>
          Add New Category
        </button>
      )}
      <div className="category-grid">
        {categories.map(category => (
          <div key={category.id} className="category-card">
            <h3>{category.name}</h3>
            {isAdmin && (
              <div className="admin-controls">
                <button onClick={() => navigate(`/admin/categories/edit/${category.id}`)}>
                  Edit
                </button>
                <button onClick={() => handleDelete(category.id)}>
                  Delete
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoriesListPage;
