// src/components/services/ServiceForm.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import ServiceService from '../../services/ServiceService';
import CategoryService from '../../services/CategoryService';
import { useAuth } from '../../AuthContext';

const ServiceForm = () => {
  const { id } = useParams();
  const isEditMode = Boolean(id);
  const { token } = useAuth();
  const navigate = useNavigate();

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    subCategory: '',
    capabilities: [''],
    constraints: '',
    cost: 0,
    deliveryTime: ''
  });
  
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch categories and existing service data (if edit mode)
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch categories
        const categoriesData = await CategoryService.getAllCategories(token);
        setCategories(categoriesData);

        // Fetch service data if in edit mode
        if (isEditMode) {
          const serviceData = await ServiceService.getServiceById(id, token);
          setFormData({
            ...serviceData,
            category: serviceData.category?.id || '',
            deliveryTime: serviceData.deliveryTime?.split('T')[0] || ''
          });
        }
      } catch (err) {
        setError('Failed to load form data');
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, [id, token, isEditMode]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleCapabilityChange = (index, value) => {
    const newCapabilities = [...formData.capabilities];
    newCapabilities[index] = value;
    setFormData(prev => ({ ...prev, capabilities: newCapabilities }));
  };

  const addCapabilityField = () => {
    setFormData(prev => ({
      ...prev,
      capabilities: [...prev.capabilities, '']
    }));
  };

  const removeCapabilityField = (index) => {
    const newCapabilities = formData.capabilities.filter((_, i) => i !== index);
    setFormData(prev => ({ ...prev, capabilities: newCapabilities }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const servicePayload = {
        ...formData,
        category: { id: formData.category },
        capabilities: formData.capabilities.filter(c => c.trim() !== '')
      };

      if (isEditMode) {
        await ServiceService.updateService(id, servicePayload, token);
      } else {
        await ServiceService.createService(servicePayload, token);
      }
      navigate('/admin/services');
    } catch (err) {
      setError('Failed to save service. Please check your inputs.');
    }
  };

  if (loading) return <div>Loading form...</div>;

  return (
    <div className="service-form">
      <h2>{isEditMode ? 'Edit' : 'Create'} Service</h2>
      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Service Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Category:</label>
          <select
            name="category"
            value={formData.category}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a Category</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Subcategory:</label>
          <input
            type="text"
            name="subCategory"
            value={formData.subCategory}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group">
          <label>Capabilities:</label>
          {formData.capabilities.map((capability, index) => (
            <div key={index} className="capability-input">
              <input
                type="text"
                value={capability}
                onChange={(e) => handleCapabilityChange(index, e.target.value)}
              />
              <button
                type="button"
                onClick={() => removeCapabilityField(index)}
                disabled={formData.capabilities.length === 1}
              >
                ×
              </button>
            </div>
          ))}
          <button type="button" onClick={addCapabilityField}>
            Add Capability
          </button>
        </div>

        <div className="form-group">
          <label>Constraints:</label>
          <textarea
            name="constraints"
            value={formData.constraints}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group">
          <label>Cost ($):</label>
          <input
            type="number"
            step="0.01"
            name="cost"
            value={formData.cost}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Delivery Date:</label>
          <input
            type="date"
            name="deliveryTime"
            value={formData.deliveryTime}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-actions">
          <button type="submit">Save Service</button>
          <button type="button" onClick={() => navigate('/admin/services')}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ServiceForm;
