// src/components/services/ServiceCatalog.js
import React, { useState, useEffect } from 'react';
import ServiceService from '../../services/ServiceService';
import { useAuth } from '../../AuthContext';

const ServiceCatalog = () => {
  const [catalog, setCatalog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { token } = useAuth();

  useEffect(() => {
    const fetchCatalog = async () => {
      try {
        const data = await ServiceService.getServiceCatalog(token);
        setCatalog(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch catalog');
        setLoading(false);
      }
    };
    fetchCatalog();
  }, [token]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Service Catalog</h1>
      <p>Last Updated: {new Date(catalog.lastUpdated).toLocaleDateString()}</p>
      <div className="catalog">
        {catalog.services.map(service => (
          <div key={service.id} className="catalog-item">
            <h3>{service.name}</h3>
            <p>Category: {service.category?.name}</p>
            <p>Subcategory: {service.subCategory}</p>
            <p>Cost: ${service.cost}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ServiceCatalog;
