// src/components/services/ServiceDetailPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import ServiceService from './ServiceService';

const ServiceDetailPage = () => {
  const { id } = useParams();
  const [service, setService] = useState(null);

  useEffect(() => {
    const fetchService = async () => {
      try {
        const data = await ServiceService.getServiceById(id);
        setService(data);
      } catch (error) {
        console.error('Error fetching service:', error);
      }
    };
    fetchService();
  }, [id]);

  if (!service) return <div>Loading...</div>;

  // In ServiceDetailPage.js
return (
  <div className="service-detail">
    <h1>{service.name}</h1>
    <p>{service.description}</p>
    <p>Category: {service.category?.name || 'General'}</p>
    {service.subCategory && <p>Sub-category: {service.subCategory}</p>}
    <p>Price: ${service.price}</p>
    <Link to="/services">Back to Catalog</Link>
  </div>
);
};

export default ServiceDetailPage;
