// src/services/ServiceService.js
import axios from 'axios';

class ServiceService {
  static BASE_URL = 'http://localhost:7070/api/services';

  static async getAllServices() {
    try {
      const response = await axios.get(`${ServiceService.BASE_URL}/all`);
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async createService(serviceData, token) {
    try {
      const response = await axios.post(`${ServiceService.BASE_URL}/add`, serviceData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async getServiceById(id) {
    try {
      const response = await axios.get(`${ServiceService.BASE_URL}/${id}`);
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async updateService(id, serviceData, token) {
    try {
      const response = await axios.put(`${ServiceService.BASE_URL}/update/${id}`, serviceData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async deleteService(id, token) {
    try {
      const response = await axios.delete(`${ServiceService.BASE_URL}/delete/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async searchServices(query) {
    try {
      const response = await axios.get(`${ServiceService.BASE_URL}/search`, {
        params: { query }
      });
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async classifyServices() {
    try {
      const response = await axios.get(`${ServiceService.BASE_URL}/classify`);
      return response.data;
    } catch (err) {
      throw err;
    }
  }

  static async getServiceCatalog() {
    try {
      const response = await axios.get(`${ServiceService.BASE_URL}/catalog`);
      return response.data;
    } catch (err) {
      throw err;
    }
  }
}

export default ServiceService;
