// In ServiceForm.js
import React, { useState, useEffect, useContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import ServiceService from './ServiceService';
import { AuthContext } from '../../AuthContext';

const ServiceForm = () => {
  const { id } = useParams();
  const { token } = useContext(AuthContext);
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [service, setService] = useState({
    name: '',
    description: '',
    categoryId: '',
    subCategory: '',
    price: ''
  });

  useEffect(() => {
    // Fetch categories
    const fetchCategories = async () => {
      try {
        const data = await ServiceService.getCategories();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };
    fetchCategories();

    // Fetch service if editing
    if (id) {
      const fetchService = async () => {
        try {
          const data = await ServiceService.getServiceById(id);
          setService({
            ...data,
            categoryId: data.category?.id || ''
          });
        } catch (error) {
          console.error('Error fetching service:', error);
        }
      };
      fetchService();
    }
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const serviceData = {
        ...service,
        category: { id: service.categoryId }
      };

      if (id) {
        await ServiceService.updateService(id, serviceData, token);
      } else {
        await ServiceService.createService(serviceData, token);
      }
      navigate('/admin/services');
    } catch (error) {
      console.error('Error saving service:', error);
    }
  };

  const handleChange = (e) => {
    setService({ ...service, [e.target.name]: e.target.value });
  };

  return (
    <div className="form-container">
      <h2>{id ? 'Edit Service' : 'Add New Service'}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={service.name}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label>Category:</label>
          <select
            name="categoryId"
            value={service.categoryId}
            onChange={handleChange}
            required
          >
            <option value="">Select Category</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Sub-category:</label>
          <input
            type="text"
            name="subCategory"
            value={service.subCategory}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Price:</label>
          <input
            type="number"
            name="price"
            value={service.price}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="btn-submit">
          {id ? 'Update' : 'Create'} Service
        </button>
      </form>
    </div>
  );
};

export default ServiceForm;
