// src/components/auth/LoginPage.jsx
import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../service/UserService';
import { AuthContext } from '../../AuthContext';
import { FiUser, FiLock } from 'react-icons/fi';

function LoginPage() {
  // Existing state and logic remains unchanged
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Existing submit logic remains the same
  };

  return (
    <div className="login-container">
      <div className="background-wrapper">
        <div className="stellar-gradient"></div>
        <div className="sun-effect"></div>
        <div className="stars"></div>
      </div>

      <div className="glass-card">
        <div className="login-header">
          <h1>Welcome Back</h1>
          <p>Access your TenderLink account</p>
        </div>

        {error && (
          <div className="error-message">
            ⚠️ {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="input-group">
            <FiUser className="input-icon" />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="input-group">
            <FiLock className="input-icon" />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>

          <button type="submit" className="login-button">
            Sign In
          </button>
        </form>

        <div className="auth-footer">
          <span>New to TenderLink? </span>
          <a href="/register" className="register-link">
            Create Account
          </a>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
