// src/AuthContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
export const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

export function AuthProvider({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [role, setRole] = useState(localStorage.getItem('role') || '');

  useEffect(() => {
    // Whenever localStorage changes (on page refresh or manual edits), sync state
    const token = localStorage.getItem('token');
    const storedRole = localStorage.getItem('role');
    setIsAuthenticated(!!token);
    setRole(storedRole || '');
  }, []);

  // Call this to log the user in and update global state + localStorage
  const login = (token, userRole) => {
    localStorage.setItem('token', token);
    localStorage.setItem('role', userRole);
    setIsAuthenticated(true);
    setRole(userRole);
  };

  // Call this to log the user out and update global state + localStorage
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setIsAuthenticated(false);
    setRole('');
  };

  // A helper to check if the user is an admin
  const isAdmin = role === 'ADMIN';

  return (
    <AuthContext.Provider value={{ isAuthenticated, role, isAdmin, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
