// src/components/services/ServiceManagementPage.js
import React, { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import ServiceService from './ServiceService';
import { AuthContext } from '../AuthContext';

const ServiceManagementPage = () => {
  const [services, setServices] = useState([]);
  const { token, isAdmin } = useContext(AuthContext);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const data = await ServiceService.getAllServices();
        setServices(data);
      } catch (error) {
        console.error('Error fetching services:', error);
      }
    };
    fetchServices();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      try {
        await ServiceService.deleteService(id, token);
        setServices(services.filter(service => service.id !== id));
      } catch (error) {
        console.error('Error deleting service:', error);
      }
    }
  };

  if (!isAdmin) return <div>Unauthorized access</div>;

  return (
    <div className="management-container">
      <h2>Services Management</h2>
      <Link to="/services/add" className="btn-add">Add New Service</Link>
      <table className="management-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {services.map(service => (
            <tr key={service.id}>
              <td>{service.name}</td>
              <td>{service.category}</td>
              <td>
                <Link to={`/services/edit/${service.id}`} className="btn-edit">Edit</Link>
                <button onClick={() => handleDelete(service.id)} className="btn-delete">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ServiceManagementPage;
