// src/App.js
import React, { useContext } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/common/Navbar';
import LoginPage from './components/auth/LoginPage';
import RegistrationPage from './components/auth/RegistrationPage';
import FooterComponent from './components/common/Footer';
import UpdateUser from './components/userspage/UpdateUser';
import UserManagementPage from './components/userspage/UserManagementPage';
import ProfilePage from './components/userspage/ProfilePage';

// Import the AuthContext stuff
import { AuthProvider, AuthContext } from './AuthContext';

function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <div className="App">
          <Navbar />
          <div className="content">
            <AppRoutes />
          </div>
          <FooterComponent />
        </div>
      </BrowserRouter>
    </AuthProvider>
  );
}

// Separate Routes into a child component so we can use context
function AppRoutes() {
  const { isAuthenticated, isAdmin } = useContext(AuthContext);

  return (
    <Routes>
      {/* If user is already authenticated, redirect from "/" or "/login" to "/profile" */}
      <Route path="/" element={
        isAuthenticated ? <Navigate to="/profile" /> : <LoginPage />
      } />
      <Route path="/login" element={
        isAuthenticated ? <Navigate to="/profile" /> : <LoginPage />
      } />
      <Route path="/profile" element={
        isAuthenticated ? <ProfilePage /> : <Navigate to="/" />
      } />

      {/* Admin-only routes */}
      {isAdmin && (
        <>
          <Route path="/register" element={<RegistrationPage />} />
          <Route path="/admin/user-management" element={<UserManagementPage />} />
          <Route path="/update-user/:userId" element={<UpdateUser />} />
        </>
      )}

      {/* Catch-all for undefined routes */}
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
