import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../AuthContext';
import './Sidebar.css'; // Make sure to create this CSS file

const Sidebar = () => {
  const { logout, isAdmin } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      logout();
      navigate('/');
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <Link to="/profile" className="sidebar-title">
          TinderLink
        </Link>
      </div>
      <ul className="sidebar-menu">
        <li className="sidebar-item">
          <Link to="/profile" className="sidebar-link">
            Profile
          </Link>
        </li>
        {isAdmin && (
  <>
    <li className="sidebar-item">
      <Link to="/admin/user-management" className="sidebar-link">
        User Management
      </Link>
    </li>
    <li className="sidebar-item">
      <Link to="/admin/services" className="sidebar-link">
        Services Management
      </Link>
    </li>
    <li className="sidebar-item">
      <Link to="/admin/categories" className="sidebar-link">
        Categories Management
      </Link>
    </li>
    
  </>
)}
        <li className="sidebar-item">
          <button onClick={handleLogout} className="sidebar-button">
            Logout
          </button>
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
