// src/components/services/ServiceDetailPage.js
import React, { useState, useEffect } from 'react';
import ServiceService from '../../services/ServiceService';
import { useAuth } from '../../AuthContext';
import { useParams, useNavigate } from 'react-router-dom';

const ServiceDetailPage = () => {
  const { id } = useParams();
  const [service, setService] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { token, isAdmin } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchService = async () => {
      try {
        const data = await ServiceService.getServiceById(id, token);
        setService(data);
        setLoading(false);
      } catch (err) {
        setError('Service not found');
        setLoading(false);
      }
    };
    fetchService();
  }, [id, token]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="service-detail">
      <h1>{service.name}</h1>
      <div className="service-info">
        <p>Category: {service.category?.name}</p>
        <p>Subcategory: {service.subCategory}</p>
        <p>Cost: ${service.cost}</p>
        <p>Delivery Time: {new Date(service.deliveryTime).toLocaleDateString()}</p>
        <h4>Capabilities:</h4>
        <ul>
          {service.capabilities?.map((capability, index) => (
            <li key={index}>{capability}</li>
          ))}
        </ul>
        <p>Constraints: {service.constraints}</p>
      </div>
      {isAdmin && (
        <div className="admin-actions">
          <button onClick={() => navigate(`/admin/services/edit/${id}`)}>
            Edit Service
          </button>
        </div>
      )}
    </div>
  );
};

export default ServiceDetailPage;
