// src/components/services/ServiceSearchPage.js
import React, { useState, useEffect } from 'react';
import ServiceService from '../../services/ServiceService';
import { useAuth } from '../../AuthContext';
import { useNavigate } from 'react-router-dom';
import './ServiceSearchPage.css';

const ServiceSearchPage = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const { token } = useAuth();
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await ServiceService.searchServices(query, token);
      setResults(data);
    } catch (err) {
      console.error('Search failed:', err);
    }
    setLoading(false);
  };

  return (
    <div className="service-search">
      <h1>Search Services</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search services..."
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </form>

      {loading && <div className="search-loading">Searching services...</div>}
      {!loading && results.length === 0 && (
        <div className="no-results">No services found matching your search</div>
      )}

      <div className="search-results">
        {results.map(service => (
          <div key={service.id} className="service-result">
            <h3>{service.name}</h3>
            <p>{service.category?.name}</p>
            <button onClick={() => navigate(`/services/${service.id}`)}>
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ServiceSearchPage;
