// src/components/services/ServicesList.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ServiceService from '../../services/ServiceService';
import { useAuth } from '../../AuthContext';
import './ServicesList.css';

const ServicesList = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { token, isAdmin } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const data = await ServiceService.getAllServices(token);
        setServices(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch services');
        setLoading(false);
      }
    };
    fetchServices();
  }, [token]);

  const handleDelete = async (id) => {
    try {
      await ServiceService.deleteService(id, token);
      setServices(services.filter(service => service.id !== id));
    } catch (err) {
      setError('Failed to delete service');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Services</h1>
      {isAdmin() && (<button onClick={() => navigate('/services/new')}>
      Add New Service
      </button>)}
      <div className="service-list">
      {services.map(service => (
  <div key={service.id} className="service-card">
    <h3>{service.name}</h3>
    <p>Category: {service.category?.name || 'Uncategorized'}</p>
    <p>Cost: ${service.cost.toFixed(2)}</p>
            <button onClick={() => window.location.href=`/services/${service.id}`}>View Details</button>
            {isAdmin() && (
              <>
                <button onClick={() => window.location.href=`/services/edit/${service.id}`}>Edit</button>
                <button onClick={() => handleDelete(service.id)}>Delete</button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ServicesList;
