// src/components/categories/CategoryForm.js
import React, { useState } from 'react';
import CategoryService from '../../services/CategoryService';
import { useAuth } from '../../AuthContext';

const CategoryForm = ({ categoryId }) => {
  const [name, setName] = useState('');
  const [error, setError] = useState('');
  const { token } = useAuth();

  useEffect(() => {
    if (categoryId) {
      const fetchCategory = async () => {
        try {
          const category = await CategoryService.getCategoryById(categoryId, token);
          setName(category.name);
        } catch (err) {
          setError('Failed to load category');
        }
      };
      fetchCategory();
    }
  }, [categoryId, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (categoryId) {
        await CategoryService.updateCategory(categoryId, { name }, token);
      } else {
        await CategoryService.createCategory({ name }, token);
      }
      window.location.href = '/categories';
    } catch (err) {
      setError('Failed to save category');
    }
  };

  return (
    <div>
      <h2>{categoryId ? 'Edit' : 'New'} Category</h2>
      {error && <div className="error">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default CategoryForm;
