import React from 'react';
import { BrowserRouter, Routes, Route, Navigate, Outlet } from 'react-router-dom';
import LoginPage from './components/auth/LoginPage';
import RegistrationPage from './components/auth/RegistrationPage';
import ProfilePage from './components/userspage/ProfilePage';
import UserManagementPage from './components/userspage/UserManagementPage';
import UpdateUser from './components/userspage/UpdateUser';
import { AuthProvider, AuthContext } from './AuthContext';
import Navbar from './components/common/Navbar';
import './App.css';

function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<PublicRoutes />} />

          {/* Protected routes using RequireAuth */}
          <Route element={<RequireAuth />}>
            {/* Protected routes with sidebar but without footer */}
            <Route element={<SidebarLayout />}>
              <Route path="/profile" element={<ProfilePage />} />
              <Route path="/register" element={<RegistrationPage />} />
              <Route path="/admin/user-management" element={<UserManagementPage />} />
              <Route path="/update-user/:userId" element={<UpdateUser />} />
            </Route>
          </Route>

          {/* Redirect unknown routes */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </BrowserRouter>
    </AuthProvider>
  );
}

// PublicRoutes shows Navbar (for unauthenticated pages)
function PublicRoutes() {
  const { isAuthenticated } = React.useContext(AuthContext);
  return isAuthenticated ? <Navigate to="/profile" /> : <Navbar><LoginPage /></Navbar>;
}

// RequireAuth checks authentication and renders nested routes via Outlet
function RequireAuth() {
  const { isAuthenticated } = React.useContext(AuthContext);
  return isAuthenticated ? <Outlet /> : <Navigate to="/" />;
}

// New layout with sidebar but without footer
function SidebarLayout() {
  return (
    <div className="dashboard-layout">
      {/* Assuming you have a sidebar component */}
      <aside className="sidebar">{/* Sidebar content */}</aside>
      <main className="main-content">
        <Outlet />
      </main>
    </div>
  );
}

export default App;
