import React, { useEffect, useState } from 'react';
import ServiceService from '../../services/ServiceService';
import { useAuth } from '../../AuthContext';
import { Link } from 'react-router-dom';

const ServicesList = () => {
  const [services, setServices] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const { authToken, isAdmin } = useAuth();

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const data = await ServiceService.getAllServices();
      setServices(data);
    } catch (error) {
      console.error('Error fetching services:', error);
    }
  };

  const handleSearch = async () => {
    try {
      const data = await ServiceService.searchServices(searchQuery);
      setServices(data);
    } catch (error) {
      console.error('Error searching services:', error);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure?')) return;
    try {
      await ServiceService.deleteService(id, authToken);
      fetchServices();
    } catch (error) {
      console.error('Error deleting service:', error);
    }
  };

  return (
    <div>
      <h1>Services</h1>
      <input
        type="text"
        placeholder="Search..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {isAdmin && (
        <Link to="/services/new">
          <button>Add Service</button>
        </Link>
      )}
      {services.map((service) => (
        <div key={service.id}>
          <h3>{service.name}</h3>
          <p>Category: {service.category?.name}</p>
          <Link to={`/services/edit/${service.id}`}>
            <button>Edit</button>
          </Link>
          <button onClick={() => handleDelete(service.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default ServicesList;
