import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import ServiceService from './ServiceService';

const ServicesCatalogPage = () => {
  const [catalog, setCatalog] = useState([]); // Changed to array

  useEffect(() => {
    const fetchCatalog = async () => {
      try {
        const data = await ServiceService.classifyServices();
        // Convert object to array if necessary
        const catalogArray = Array.isArray(data) ? data : Object.entries(data).map(([category, services]) => ({
          category,
          services
        }));
        setCatalog(catalogArray);
      } catch (error) {
        console.error('Error fetching catalog:', error);
      }
    };
    fetchCatalog();
  }, []);

  // In ServicesCatalogPage.js
return (
  <div className="catalog-container">
    <h1>Service Catalog</h1>
    {catalog.map((categoryGroup) => (
      <div key={categoryGroup.category} className="category-section">
        <h2>{categoryGroup.category}</h2>
        <div className="services-list">
          {categoryGroup.services.map(service => (
            <div key={service.id} className="service-card">
              <h3>{service.name}</h3>
              <p>{service.description}</p>
              <p>Category: {service.category?.name || 'General'}</p>
              <p>Price: ${service.price}</p>
              <Link to={`/services/${service.id}`}>View Details</Link>
            </div>
          ))}
        </div>
      </div>
    ))}
  </div>
);
};

export default ServicesCatalogPage;
